import { React, useState } from 'react';

const Lang = () => {

    const [text, setText] = useState('')

    const find = (e) => {
        if(e.key === 'Enter'){
            var DetectLanguage = require('detectlanguage')
            var detectlanguage = new DetectLanguage("e291e72cac0af694cf437df95a050196")
            detectlanguage.detect(text).then(function(result){
                console.log(JSON.stringify(result))
            })
        }
    }

    return (
        <div>
            <div className="body">
                <br/>
                <div className="box">
                    <input type="text" placeholder="Your Text Here..." onChange={ e => setText(e.target.value)} value={text} onKeyPress={find()} required autocomplete="off"/><br/>
                </div>
                <h4>Trending language searches...</h4>
                <pre>{text}</pre>
            </div>
        </div>
    )
}

export default Lang;
